    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}User{% endblock %}

{% block body %}
    <h1>Détails de l'utilisateur #{{ user.id }}</h1>

    <table class=\"table\">
        <tbody>
            <tr>
                <th>Email</th>
                <td>{{ user.email }}</td>
            </tr>
            <tr>
                <th>Rôles</th>
                <td>{{ user.roles ? user.roles|json_encode : '' }}</td>
            </tr>
            <tr>
                <th>Mot de passe</th>
                <td>{{ user.password }}</td>
            </tr>
            <tr>
                <th>Prénom</th>
                <td>{{ user.firstName }}</td>
            </tr>
            <tr>
                <th>Nom</th>
                <td>{{ user.lastName }}</td>
            </tr>
            <tr>
                <th>Date de création</th>
                <td>{{ user.createdAt ? user.createdAt|date('d/m/Y - H:i:s') : '' }}</td>
            </tr>
        </tbody>
    </table>

    <div class=\"btnContainer\">

        <div class=\"btn\"><div><a href=\"{{ path('app_user_index') }}\">Retour</a></div></div>

        <div class=\"btn\"><div><a href=\"{{ path('app_user_edit', {'id': user.id}) }}\">Modifier</a></div></div>

    </div>

    {{ include('user/_delete_form.html.twig') }}
{% endblock %}
", "user/show.html.twig", "C:\\symfony\\spbudget\\templates\\user\\show.html.twig");
